import React,{ useState } from 'react';
import {Component} from 'react';
import axios from 'axios';
import './form.css';

class Form extends Component {
    constructor(props: any) {
        super(props);
    }

    state = {
        isEdit: false,
        id: 0,
        coffee_type: '',
        coffeePrice: null,
        description: '',
        image_source: "C:\fakepath\mocha-latte.jpeg",
    }


    onSubmit = (e: any) => {
        e.preventDefault();
        console.log(e.target);
        this.setState({
            image: e.target.image_source,
            description : e.target.description,
            coffee_type : e.target.coffee_type
        })
        console.log(this.state);


        axios.post("localhost:3000/graphql", this.state, {
          headers: {
            "Content-Type": "multipart/form-data" }
        })
        .then((response) => {
        //   this.setState({
        //       description: '',
        //       coffee_type: '',
        //       image_source: ''
        //     });
        })
        .catch((error) => {
          console.log(error);
          this.setState({
            description: '',
            coffee_type: '',
            image_source: '',
          });
        });

        e.target.image_source.value = "";
        e.target.image_source.value = "";
        e.target.image_source.value = "";
    }

    componentDidUpdate() {
        // console.log(this.state);
    }

    getData(val: any) {
        this.setState({ [val.target.name]: val.target.value })
    }

    render() {
        return (
            <>
                <div className="form-wrapper">
                    <div className="title">
                        <p> Barnsley Brew Coffee </p>
                    </div>
                    <form className="form" onSubmit={this.onSubmit} method='POST'>
                        <div className="img_preview">
                        </div>
                        <img className="image" src={this.state.image_source} />
                        <div className="form_group">
                            <label htmlFor="choose-file">Choose Coffee Image </label>
                            <input onChange={(e) => this.getData(e)} value={this.state.image_source} type="file" id="choose_file" className="form_control" name='image_source' />
                        </div>

                        <div className="form_group">
                            <label htmlFor="coffee_name">Coffee Name</label>
                            <input onChange={(e) => this.getData(e)} type="text" id="coffee_name" className="form_control" value={this.state.coffee_type}  name='coffee_type' />
                        </div>

                        <div className="form_group">
                            <label htmlFor="coffee_description"> Description </label>
                            <input onChange={(e) => this.getData(e)} type="text" id="coffee_description" className="form_control" value={this.state.description}  name='description' />
                        </div>

                        <div className="button">
                            <button className="submit"> Submit </button>
                        </div>
                    </form>
                </div>
            </>
        );
    }
}
export default Form;
